import React, { useCallback, useState } from "react";
import { Card, Col, Empty, Input, Row, Spin, Image } from "antd";
import { getUserRoomList } from "/@/service/api/userRoom";
import InfiniteScroll from "react-infinite-scroller";
import { RoomItemInfoProps } from "/@/service/types/classRoom";

const { Meta } = Card;
const { Search } = Input;

const UserRoomsDemo: React.FC = () => {
  const [name, setName] = useState<string>("");
  const [items, setItems] = useState<Array<RoomItemInfoProps>>([]);
  let [page, setPage] = useState<number>(1);
  const [hasMore, setHasMore] = useState<boolean>(true);
  // 获取房间
  const fetchItems = useCallback(async () => {
    const res = await getUserRoomList({ pageNum: page, roomNameRule: name });
    if (res?.data?.length) {
      const itemsArr = [...items, ...res.data];
      setItems(itemsArr);
      setPage((page += 1));
      if (itemsArr?.length >= res?.data?.[0]?.classroomInfo?.total_count) {
        setHasMore(false);
      }
    } else {
      setHasMore(false);
    }
  }, [items, page]);

  // 重置
  const reload = () => {
    setPage(1);
    setItems([]);
    setHasMore(true);
  };

  return (
    <div style={{ paddingBottom: 30 }}>
      <div style={{ maxWidth: 300, flexShrink: 0, padding: "20px 0px" }}>
        <Search
          placeholder="search rooms"
          allowClear
          enterButton="Search"
          size="large"
          onInput={(e) => setName(e.currentTarget.value)}
          onSearch={() => {
            reload();
          }}
        />
      </div>
      <InfiniteScroll
        loadMore={fetchItems}
        hasMore={hasMore}
        loader={
          <div
            style={{
              width: "100%",
              display: "flex",
              justifyContent: "center",
              paddingTop: 30,
            }}
            key={0}
          >
            <Spin />
          </div>
        }
      >
        <Row gutter={[12, 12]} wrap={true}>
          {items?.length ? (
            items.map((item, index) => {
              return (
                <Col
                  className="gutter-row"
                  sm={12}
                  md={12}
                  lg={8}
                  xl={6}
                  xxl={4}
                  key={index}
                >
                  <Card
                    cover={
                      <Image
                        height={200}
                        src={item?.imageData}
                        preview={false}
                        style={{ padding: "3px", objectFit: "fill" }}
                        fallback="data:image/png;base64,"
                      />
                    }
                    bordered
                  >
                    <Meta
                      title={`Maximun capacity：${item?.classroomInfo?.capacity}`}
                      description={`Location：${item?.classroomInfo?.floor}`}
                    />
                  </Card>
                </Col>
              );
            })
          ) : (
            <Empty description="No data" style={{ margin: "100px auto" }} />
          )}
        </Row>
      </InfiniteScroll>
    </div>
  );
};

export default UserRoomsDemo;
